mapper.select:
/* getMovieList */ SELECT M.MOVIE_ID , TITLE , BACKDROP_PATH , GENRE_IDS , ORIGINAL_TITLE , RELEASE_YEAR , ORIGINAL_LANGUAGE , RUNTIME , POSTER_PATH , VOTE_AVERAGE , VOTE_COUNT , TAGLINE , OVERVIEW , POPULARITY , MG.GROUP_NUM , PRODUCTION_COUNTRIES FROM MOVIE M JOIN MOVIE_GROUP MG ON M.MOVIE_ID = MG.MOVIE_ID
AND MG.GROUP_NUM = #{groupNum}
AND UPPER(REPLACE(TITLE, ' ', '')) LIKE CONCAT('%', UPPER(REPLACE(#{schTitle}, ' ', '')), '%')
ORDER BY POPULARITY DESC

mapper.insert:

/*	insertMovie	*/
INSERT IGNORE INTO MOVIE
(
      MOVIE_ID
    , TITLE
    , BACKDROP_PATH
    , GENRE_IDS
    , ORIGINAL_TITLE
    , RELEASE_YEAR
    , ORIGINAL_LANGUAGE
    , RUNTIME
    , POSTER_PATH
    , VOTE_AVERAGE
    , VOTE_COUNT
    , TAGLINE
    , OVERVIEW
    , POPULARITY
    , PRODUCTION_COUNTRIES
    , INSERT_TIME
) VALUES(
    #{movieId}
    , #{title}
    , #{backdropPath}
    , #{genreIds}
    , #{originalTitle}
    , #{releaseYear}
    , #{originalLanguage}
    , #{runtime}
    , #{posterPath}
    , #{voteAverage}
    , #{voteCount}
    , #{tagline}
    , #{overview}
    , #{popularity}
    , #{productionCountries}
    , NOW()
)


/*	insertMovieGroup	*/
INSERT INTO MOVIE_GROUP
(
      MOVIE_ID
    , GROUP_NUM
    , MOVIE_RANK
) VALUES(
    #{movieId}
    , #{groupNum}
    , #{movieRank}
)

mapper.select:
/* getMovieDetail */ SELECT M.MOVIE_ID , TITLE , BACKDROP_PATH , GENRE_IDS , ORIGINAL_TITLE , RELEASE_YEAR , ORIGINAL_LANGUAGE , RUNTIME , POSTER_PATH , VOTE_AVERAGE , VOTE_COUNT , TAGLINE , OVERVIEW , POPULARITY , PRODUCTION_COUNTRIES FROM MOVIE M JOIN MOVIE_GROUP MG ON M.MOVIE_ID = MG.MOVIE_ID
AND M.MOVIE_ID = #{movieId}
LIMIT 1

/* getMovieSchListAjax */ SELECT MOVIE_ID , TITLE FROM MOVIE
AND UPPER(REPLACE(TITLE, ' ', '')) LIKE CONCAT('%', UPPER(REPLACE(#{schTitle}, ' ', '')), '%')
ORDER BY POPULARITY DESC LIMIT 10
